import React, { useState, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { observer } from 'mobx-react-lite'; // Импорт observer
import Button from '../Button/Button';
import classes from './AuthForm.module.css';
import { Context } from '../../storage'; // Укажите правильный путь к вашему Context
import { ROUTE } from '../../utils/consts'; // Предполагается, что у вас есть файл с константами роутов

const AuthForm = observer(({ isLogin = false }) => { // Обертка в observer
  const { userStore } = useContext(Context);
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    username: '', // Добавлено поле username для логина
    firstName: '',
    lastName: '',
    email: '',
    password: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    let success = false;
    if (isLogin) {
      success = await userStore.login(formData.username, formData.password);
    } else {
      const userData = {
        username: formData.username,
        firstName: formData.firstName,
        lastName: formData.lastName,
        email: formData.email,
        password: formData.password
      };
      success = await userStore.register(userData);
    }

    if (success) {
      navigate(ROUTE.FEED); // Перенаправление на главную страницу после успеха
    } else {
      // Ошибка будет в userStore.error, можно отобразить ее
      alert(`Error: ${userStore.error}`);
    }
  };

  return (
    <div className={classes.container}>
      <div className={classes.logo}>Welcome</div>
      <h1 className={classes.title}>{isLogin ? 'Sign in to your account' : 'Create your account'}</h1>
      <form onSubmit={handleSubmit} className={classes.form}>
        {!isLogin && (
          <>
            <div className={classes.formGroup}>
              <label htmlFor="firstName" className={classes.label}>First Name</label>
              <input type="text" id="firstName" name="firstName" value={formData.firstName} onChange={handleChange} className={classes.input} required={!isLogin} />
            </div>
            <div className={classes.formGroup}>
              <label htmlFor="lastName" className={classes.label}>Last Name</label>
              <input type="text" id="lastName" name="lastName" value={formData.lastName} onChange={handleChange} className={classes.input} required={!isLogin}/>
            </div>
            <div className={classes.formGroup}> {/* Email нужен и для регистрации, и для логина, если логин по email */}
              <label htmlFor="email" className={classes.label}>Email</label>
              <input type="email" id="email" name="email" value={formData.email} onChange={handleChange} className={classes.input} required />
            </div>
          </>
        )}

        {/* Username для логина и регистрации */}
        <div className={classes.formGroup}>
          <label htmlFor="username" className={classes.label}>Username</label>
          <input type="text" id="username" name="username" value={formData.username} onChange={handleChange} className={classes.input} required />
        </div>

        {/* Если логин по email, то для isLogin email должен быть выше, а username не нужен */}
        {/* Если логин по username, то email для isLogin не нужен */}
        {/* Сейчас username обязателен для обоих, email только для регистрации. Это нужно согласовать с бэкендом. */}
        {/* Предположим, логин по username, как в UserLoginDto */}
        {isLogin && (
             <div className={classes.formGroup}>
              <label htmlFor="emailForLogin" className={classes.label}>Email (optional, for display only if logic changes)</label>
              <input type="email" id="emailForLogin" name="email" value={formData.email} onChange={handleChange} className={classes.input} />
            </div>
        )}


        <div className={classes.formGroup}>
          <label htmlFor="password" className={classes.label}>Password</label>
          <input type="password" id="password" name="password" value={formData.password} onChange={handleChange} className={classes.input} required minLength={6} />
        </div>

        <Button type="submit" className={classes.submitButton} disabled={userStore.loading}>
          {userStore.loading ? 'Processing...' : (isLogin ? 'Sign in' : 'Create Account')}
        </Button>
      </form>

      {userStore.error && <p style={{color: 'red', textAlign: 'center'}}>{userStore.error}</p>}

      <div className={classes.switchAuth}>
        {isLogin ? (
          <> Don't have an account? <Link to={ROUTE.REGISTER} className={classes.link}>Sign up</Link> </>
        ) : (
          <> Already have an account? <Link to={ROUTE.LOGIN} className={classes.link}>Sign in</Link> </>
        )}
      </div>
    </div>
  );
});

export default AuthForm;
