import React, { useContext } from 'react';
import { Link } from 'react-router-dom';

import classes from './ProfilePageSideBar.module.css'
import { Context } from '../..';

const ProfilePageSideBar = (props) => {
    const { friendStorage } = useContext(Context)
    const friends = friendStorage.friends;

    return (
        <div className={classes.mainContainer}>
            <h2>Друзья</h2>
            <div className={classes.profileItems}>
                {friends.map((friend) => (
                    <div className={classes.profileItem}>
                        <div className={classes.avatarContainer}>
                            <img src={friend.avatarUrl} alt="avatar" className={classes.avatar}/>
                        </div>
                        <div className={classes.linkContainer}>
                            <Link to={`/users/${friend.id}`} className={classes.profileLink}>
                                {friend.firstName + " " + friend.lastName}
                            </Link>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ProfilePageSideBar;
