import React, { useContext, useEffect, useRef, useCallback } from 'react';
import { useLocation } from 'react-router-dom';
import { observer } from 'mobx-react-lite';
import { Context } from '../../storage';
import MainLeftSideBar from '../../component/MainLeftSideBar/MainLeftSideBar';
import PostContainer from '../../component/PostContainer/PostContainer';
import MainRightSideBar from '../../component/MainRightSideBar/MainRightSideBar';
import classes from './Main.module.css';
import { ROUTE } from '../../utils/consts';


const Main = observer(() => {
    const { postStore, friendStore, groupStore } = useContext(Context);
    const location = useLocation();

    const observer = useRef(); // Для Intersection Observer instance
    const loadMoreTriggerRef = useRef(null); // Элемент-триггер внизу списка

    useEffect(() => {
        if (friendStore.recommendedFriends.length === 0) {
            friendStore.fetchRecommendedFriends();
        }
        if (groupStore.recommendedGroups.length === 0) {
            groupStore.fetchRecommendedGroups();
        }
    }, [friendStore, groupStore]); // Зависимости

    // Начальная загрузка постов при смене роута или при первой загрузке
    useEffect(() => {
        if (location.pathname === ROUTE.RECOMMENDATIONS) {
            // Сбрасываем и загружаем первую страницу, если постов еще нет или роут изменился
            // `postStore.fetchRecommendedPosts(true)` позаботится о сбросе внутри себя
            postStore.fetchRecommendedPosts(true);
        } else if (location.pathname === ROUTE.FEED) {
            postStore.fetchFeedPosts(true);
        }
        // Для других роутов (например, страница пользователя, группы) логика будет аналогичной,
        // если там тоже нужна пагинация постов.

        // Очистка при размонтировании компонента (хотя Main обычно не размонтируется)
        // или при смене location.pathname, чтобы observer не пытался грузить для старого пути.
        return () => {
            if (observer.current) {
                observer.current.disconnect();
            }
        };
    }, [location.pathname, postStore]); // postStore добавлен для стабильности ссылки

    // Callback для Intersection Observer
    const handleObserver = useCallback((entries) => {
        const target = entries[0];
        if (target.isIntersecting) {
            if (location.pathname === ROUTE.RECOMMENDATIONS) {
                if (postStore.hasMoreRec && !postStore.loadingMoreRec) {
                    console.log("Intersecting Recommendations, loading more...");
                    postStore.fetchRecommendedPosts(); // Загрузка следующей страницы
                }
            } else if (location.pathname === ROUTE.FEED) {
                if (postStore.hasMoreFeed && !postStore.loadingMoreFeed) {
                    console.log("Intersecting Feed, loading more...");
                    postStore.fetchFeedPosts(); // Загрузка следующей страницы
                }
            }
        }
    }, [postStore, location.pathname]); // Зависимости

    // Настройка Intersection Observer
    useEffect(() => {
        const options = {
            root: null, // viewport
            rootMargin: '20px', // Начать загрузку немного раньше, чем элемент станет виден
            threshold: 0.1 // 10% видимости элемента
        };

        observer.current = new IntersectionObserver(handleObserver, options);

        const currentTrigger = loadMoreTriggerRef.current;
        if (currentTrigger) {
            observer.current.observe(currentTrigger);
        }

        return () => {
            if (currentTrigger && observer.current) {
                observer.current.unobserve(currentTrigger);
            }
        };
    }, [handleObserver, loadMoreTriggerRef.current]); // Пересоздаем, если изменился триггер или callback

    const getCurrentPostsData = () => {
        switch (location.pathname) {
            case ROUTE.RECOMMENDATIONS:
                return {
                    posts: postStore.recommendedPosts,
                    isLoadingMore: postStore.loadingMoreRec,
                    hasMoreToLoad: postStore.hasMoreRec,
                    // Начальная загрузка определяется по общему `loading` и отсутствию постов
                    isInitialLoading: postStore.loading && postStore.recommendedPosts.length === 0
                };
            case ROUTE.FEED:
            default:
                return {
                    posts: postStore.feedPosts,
                    isLoadingMore: postStore.loadingMoreFeed,
                    hasMoreToLoad: postStore.hasMoreFeed,
                    isInitialLoading: postStore.loading && postStore.feedPosts.length === 0
                };
        }
    };

    const { posts, isLoadingMore, hasMoreToLoad, isInitialLoading } = getCurrentPostsData();

    if (isInitialLoading) {
        return (
            <main className={classes.mainContainer}>
                <MainLeftSideBar />
                <div className={classes.container} style={{ textAlign: 'center', padding: '20px' }}>
                    Loading posts...
                </div>
                <MainRightSideBar
                    possibleFriends={friendStore.recommendedFriends}
                    interestingGroups={groupStore.recommendedGroups}
                />
            </main>
        );
    }

    return (
        <main className={classes.mainContainer}>
            <MainLeftSideBar />
            <div className={classes.container}>
                {posts.length > 0 ? (
                    <PostContainer posts={posts} />
                ) : (
                    !isLoadingMore && !hasMoreToLoad && <div style={{ textAlign: 'center', padding: '20px' }}>No posts to display.</div>
                )}

                {/* Элемент-триггер для Intersection Observer */}
                <div ref={loadMoreTriggerRef} className={classes.loadMoreTrigger}>
                    {isLoadingMore && <p>Loading more posts...</p>}
                    {!hasMoreToLoad && posts.length > 0 && <p>You've reached the end.</p>}
                </div>
            </div>
            <MainRightSideBar 
                possibleFriends={friendStore.recommendedFriends} 
                interestingGroups={groupStore.recommendedGroups}
            />
        </main>
    );
});

export default Main;
