import { makeAutoObservable } from "mobx";

export class FriendStorage {
    constructor() {
        this._friends = [];
        this._friendRequests = []; // Входящие заявки
        this._sentRequests = []; // Исходящие заявки
        this._recommendedFriends = []; // Рекомендованные друзья

        makeAutoObservable(this);
    }

    // Setters
    setFriends(friends) {
        this._friends = friends;
    }

    setFriendRequests(requests) {
        this._friendRequests = requests;
    }

    setSentRequests(requests) {
        this._sentRequests = requests;
    }

    setRecommendedFriends(friends) {
        this._recommendedFriends = friends;
    }

    // Getters
    get friends() {
        return this._friends;
    }

    get friendRequests() {
        return this._friendRequests;
    }

    get sentRequests() {
        return this._sentRequests;
    }

    get recommendedFriends() {
        return this._recommendedFriends;
    }
}
