import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } //, useParams // если ID не текущего юзера
from 'react-router-dom';
import { observer } from 'mobx-react-lite';
import { Context } from '../../storage'; // Путь к вашему Context
import Button from '../../component/Button/Button'; // Путь к компоненту Button
import classes from './EditProfile.module.css'; // Создайте этот CSS файл

const EditProfilePage = observer(() => {
    const { userStore } = useContext(Context);
    const navigate = useNavigate();
    // const { id } = useParams(); // Если бы редактировали чужой профиль (админом)
    const currentUser = userStore.user;

    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        avatar: null, // File object
    });
    const [avatarPreview, setAvatarPreview] = useState(null);

    useEffect(() => {
        if (currentUser && currentUser.id) {
            setFormData({
                firstName: currentUser.firstName || '',
                lastName: currentUser.lastName || '',
                email: currentUser.email || '',
                avatar: null,
            });
            setAvatarPreview(currentUser.avatarUrl || '/default_avatar.png');
        } else {
            // Если пользователь не загружен, возможно, перенаправить или показать загрузку
            // navigate(ROUTE.LOGIN); // Пример
        }
    }, [currentUser, navigate]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleAvatarChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setFormData(prev => ({ ...prev, avatar: file }));
            const reader = new FileReader();
            reader.onloadend = () => {
                setAvatarPreview(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!currentUser || !currentUser.id) return;

        const dataToUpdate = {
            firstName: formData.firstName,
            lastName: formData.lastName,
            email: formData.email,
            // Пароль здесь не меняем
        };
        const success = await userStore.updateUser(currentUser.id, dataToUpdate, formData.avatar);
        if (success) {
            navigate(`/me`); // или `/users/${currentUser.id}`
        } else {
            // Показать ошибку из userStore.error
            alert(`Error: ${userStore.error}`);
        }
    };

    if (userStore.loading && !currentUser?.id) return <div>Loading profile...</div>;
    if (!currentUser?.id) return <div>User not found. <button onClick={() => navigate(ROUTE.LOGIN)}>Login</button></div>;


    return (
        <div className={classes.container}>
            <h1 className={classes.title}>Edit Profile</h1>
            <form onSubmit={handleSubmit} className={classes.form}>
                <div className={classes.avatarSection}>
                    <img src={avatarPreview} alt="Avatar" className={classes.avatarPreview} />
                    <label htmlFor="avatarUpload" className={classes.avatarUploadLabel}>Change Avatar</label>
                    <input
                        type="file"
                        id="avatarUpload"
                        name="avatar"
                        onChange={handleAvatarChange}
                        accept="image/*"
                        className={classes.fileInput}
                    />
                </div>

                <div className={classes.formGroup}>
                    <label htmlFor="firstName" className={classes.label}>First Name</label>
                    <input
                        type="text"
                        id="firstName"
                        name="firstName"
                        value={formData.firstName}
                        onChange={handleChange}
                        className={classes.input}
                        required
                    />
                </div>

                <div className={classes.formGroup}>
                    <label htmlFor="lastName" className={classes.label}>Last Name</label>
                    <input
                        type="text"
                        id="lastName"
                        name="lastName"
                        value={formData.lastName}
                        onChange={handleChange}
                        className={classes.input}
                        required
                    />
                </div>

                <div className={classes.formGroup}>
                    <label htmlFor="email" className={classes.label}>Email</label>
                    <input
                        type="email"
                        id="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        className={classes.input}
                        required
                        // disabled // Email часто не дают менять или требуют подтверждения
                    />
                </div>

                <div className={classes.actions}>
                    <Button type="submit" disabled={userStore.loading}>
                        {userStore.loading ? 'Saving...' : 'Save Changes'}
                    </Button>
                    <Button white type="button" onClick={() => navigate(-1)} disabled={userStore.loading}>
                        Cancel
                    </Button>
                </div>
                 {userStore.error && <p className={classes.errorText}>{userStore.error}</p>}
            </form>
        </div>
    );
});

export default EditProfilePage;
