import { makeAutoObservable, runInAction } from "mobx";
import { PostAPI } from "../api/postApi";

export class PostStore {
    _feedPosts = []; // Посты для главной ленты (PostResponseDto[])
    _userPosts = []; // Посты конкретного пользователя (PostResponseDto[])
    _groupPosts = []; // Посты конкретной группы (PostResponseDto[])
    _recommendedPosts = []; // Рекомендованные посты (PostResponseDto[])
    _currentPost = null; // Текущий просматриваемый пост (PostResponseDto)
    _loading = false;
    _error = null;

    constructor(rootStore) {
        makeAutoObservable(this);
        this.rootStore = rootStore; // Для userId в createPost
        // this.fetchFeedPosts(); // Начальная загрузка
    }

    setLoading(bool) { this._loading = bool; }
    setError(error) { this._error = error; }

    // Getters
    get feedPosts() { return this._feedPosts; }
    get userPosts() { return this._userPosts; }
    get groupPosts() { return this._groupPosts; }
    get recommendedPosts() { return this._recommendedPosts; }
    get currentPost() { return this._currentPost; }
    get loading() { return this._loading; }
    get error() { return this._error; }


    async fetchFeedPosts() {
        this.setLoading(true);
        this.setError(null);
        try {
            const posts = await PostAPI.getLatest();
            runInAction(() => {
                this._feedPosts = posts.content;
            });
        } catch (e) {
            runInAction(() => this.setError(e.response?.data?.message || "Failed to fetch feed posts"));
            console.error(e);
        } finally {
            runInAction(() => this.setLoading(false));
        }
    }

    async fetchRecommendedPosts() {
        this.setLoading(true);
        this.setError(null);
        try {
            const posts = await PostAPI.getRecommended();
            runInAction(() => {
                this._recommendedPosts = posts.content;
            });
        } catch (e) {
            runInAction(() => this.setError(e.response?.data?.message || "Failed to fetch recommended posts"));
            console.error(e);
        } finally {
            runInAction(() => this.setLoading(false));
        }
    }

    async fetchUserPosts(userId) {
        this.setLoading(true);
        this.setError(null);
        try {
            const posts = await PostAPI.getPostsByUserId(userId);
            runInAction(() => {
                this._userPosts = posts.content;
            });
        } catch (e) {
            runInAction(() => this.setError(e.response?.data?.message || "Failed to fetch user posts"));
            console.error(e);
        } finally {
            runInAction(() => this.setLoading(false));
        }
    }

    async fetchGroupPosts(groupId) {
        this.setLoading(true);
        this.setError(null);
        try {
            const posts = await PostAPI.getPostsByGroupId(groupId);
            runInAction(() => {
                this._groupPosts = posts.content;
            });
        } catch (e) {
            runInAction(() => this.setError(e.response?.data?.message || "Failed to fetch group posts"));
            console.error(e);
        } finally {
            runInAction(() => this.setLoading(false));
        }
    }

    async createPost(postContent, imageFile, groupId = null) {
        const userId = this.rootStore.userStore.userId;
        if (!userId) {
            this.setError("User not authenticated");
            return false;
        }
        const postData = {
            content: postContent,
            userId: userId,
            groupId: groupId
        };

        this.setLoading(true);
        this.setError(null);
        try {
            const newPost = await PostAPI.createPost(postData, imageFile);
            runInAction(() => {
                this._feedPosts.unshift(newPost); // Добавляем в начало ленты
            if (groupId) {
                if (this.rootStore.groupStore.currentGroupDetails?.id === Number(groupId)) {
                    this.rootStore.groupStore.currentGroupDetails.posts.unshift(newPost);
                }
            } else {
                if (this.rootStore.userStore.user?.id === newPost.author.id) {
                    if(this.rootStore.userStore.user && this.rootStore.userStore.user.posts) {
                        this.rootStore.userStore.user.posts.unshift(newPost);
                    }
                }
            }
            });
            return true;
        } catch (e) {
            runInAction(() => this.setError(e.response?.data?.message || "Failed to create post"));
            console.error(e);
            return false;
        } finally {
            runInAction(() => this.setLoading(false));
        }
    }

    async deletePost(postId) {
        this.setLoading(true);
        this.setError(null);
        try {
            await PostAPI.deletePost(postId);
            runInAction(() => {
                this._feedPosts = this._feedPosts.filter(p => p.id !== postId);
                this._userPosts = this._userPosts.filter(p => p.id !== postId);
                this._groupPosts = this._groupPosts.filter(p => p.id !== postId);
                this._recommendedPosts = this._recommendedPosts.filter(p => p.id !== postId);
            });
        } catch (e) {
            runInAction(() => this.setError(e.response?.data?.message || "Failed to delete post"));
            console.error(e);
        } finally {
            runInAction(() => this.setLoading(false));
        }
    }

    _updatePostLike(postId, updatedPostData) {
        const update = (postsArray) => postsArray.map(p => p.id === postId ? {...p, ...updatedPostData} : p);
        this._feedPosts = update(this._feedPosts);
        this._userPosts = update(this._userPosts);
        this._groupPosts = update(this._groupPosts);
        this._recommendedPosts = update(this._recommendedPosts);
        if (this._currentPost?.id === postId) {
            this._currentPost = {...this._currentPost, ...updatedPostData};
        }
    }

    async likePost(postId) {
        // Оптимистичное обновление
        const originalPosts = { feed: [...this._feedPosts], user: [...this._userPosts], group: [...this._groupPosts], rec: [...this._recommendedPosts] };
        const postToUpdate = this._feedPosts.find(p => p.id === postId) || this._userPosts.find(p => p.id === postId) || this._groupPosts.find(p => p.id === postId) || this._recommendedPosts.find(p => p.id === postId);

        if (postToUpdate) {
            runInAction(() => {
                this._updatePostLike(postId, {
                    likeCount: postToUpdate.likeCount + 1,
                    likedByCurrentUser: true
                });
            });
        }

        try {
            const updatedPost = await PostAPI.likePost(postId); // API должен вернуть обновленный пост
            runInAction(() => {
                this._updatePostLike(postId, updatedPost);
            });
        } catch (e) {
            runInAction(() => { // Откат в случае ошибки
                this.setError(e.response?.data?.message || "Failed to like post");
                this._feedPosts = originalPosts.feed;
                this._userPosts = originalPosts.user;
                this._groupPosts = originalPosts.group;
                this._recommendedPosts = originalPosts.rec;
                // Восстановить _currentPost если нужно
            });
            console.error(e);
        }
    }

    async unlikePost(postId) {
        const originalPosts = { feed: [...this._feedPosts], user: [...this._userPosts], group: [...this._groupPosts], rec: [...this._recommendedPosts] };
        const postToUpdate = this._feedPosts.find(p => p.id === postId) || this._userPosts.find(p => p.id === postId) || this._groupPosts.find(p => p.id === postId) || this._recommendedPosts.find(p => p.id === postId);

        if (postToUpdate) {
             runInAction(() => {
                this._updatePostLike(postId, {
                    likeCount: postToUpdate.likeCount - 1,
                    likedByCurrentUser: false
                });
            });
        }
        try {
            const updatedPost = await PostAPI.unlikePost(postId);
            runInAction(() => {
                this._updatePostLike(postId, updatedPost);
            });
        } catch (e) {
            runInAction(() => { // Откат
                this.setError(e.response?.data?.message || "Failed to unlike post");
                this._feedPosts = originalPosts.feed;
                this._userPosts = originalPosts.user;
                this._groupPosts = originalPosts.group;
                this._recommendedPosts = originalPosts.rec;
            });
            console.error(e);
        }
    }
}
