import React, { useState, useRef, useContext } from 'react';
import classes from './CreatePost.module.css';
import { Context } from '../..';

const CreatePost = () => {
    const { userStorage } = useContext(Context);
    const user = userStorage.user;
    const [content, setContent] = useState('');
    const [image, setImage] = useState(null);
    const [imagePreview, setImagePreview] = useState(null);
    const fileInputRef = useRef(null);

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setImage(file);
            const reader = new FileReader();
            reader.onloadend = () => {
                setImagePreview(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleRemoveImage = () => {
        setImage(null);
        setImagePreview(null);
        // Сбрасываем значение input файла, чтобы можно было загрузить то же изображение снова
        if (fileInputRef.current) {
            fileInputRef.current.value = '';
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Здесь будет логика отправки поста на сервер
        console.log({ content, image });
    };

    const triggerFileInput = () => {
        fileInputRef.current.click();
    };

    return (
        <div className={classes.pageContainer}>
            <form onSubmit={handleSubmit} className={classes.postForm}>
                <div className={classes.mainContainer}>
                    {/* Заголовок поста */}
                    <div className={classes.postHeader}>
                        <div className={classes.postHeader__leftPart}>
                            <div className={classes.profilePictureContainer}>
                                <img 
                                    src={user.avatarUrl} 
                                    className={classes.profilePictureImage} 
                                    alt="avatar" 
                                />
                            </div>
                            <div className={classes.profileName}>
                                <span className={classes.profileLink}>
                                    {user.firstName + " " + user.lastName}
                                </span>
                                <p className={classes.dateOfCreation}>Сейчас</p>
                            </div>
                        </div>
                    </div>

                    <div className={classes.divider} />

                    {/* Поле ввода текста */}
                    <div className={classes.contentContainer}>
                        <textarea
                            className={classes.contentInput}
                            placeholder="What's on your mind?"
                            value={content}
                            onChange={(e) => setContent(e.target.value)}
                        />

                        {/* Превью изображения с кнопкой удаления */}
                        {imagePreview && (
                            <div className={classes.imagePreviewContainer}>
                                <div className={classes.removeImageButton} onClick={handleRemoveImage}>
                                    <CloseIcon className={classes.closeIcon} />
                                </div>
                                <img 
                                    src={imagePreview} 
                                    className={classes.image} 
                                    alt="preview" 
                                />
                            </div>
                        )}
                    </div>

                    <div className={classes.divider} />

                    {/* Футер с кнопками */}
                    <div className={classes.postFooter}>
                        <div className={classes.postFooter__leftPart}>
                            <input
                                type="file"
                                ref={fileInputRef}
                                onChange={handleImageChange}
                                accept="image/*"
                                style={{ display: 'none' }}
                            />
                            <button 
                                type="button" 
                                className={classes.mediaButton}
                                onClick={triggerFileInput}
                            >
                                Photo
                            </button>
                        </div>
                        <button 
                            type="submit" 
                            className={classes.submitButton}
                            disabled={!content && !image}
                        >
                            Post
                        </button>
                    </div>
                </div>
            </form>
        </div>
    );
};

export default CreatePost;
