import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Button from '../Button/Button';
import classes from './AuthForm.module.css';

const AuthForm = ({ isLogin = false }) => {
  const [formData, setFormData] = useState({
    username: '',
    firstName: '',
    lastName: '',
    email: '',
    password: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isLogin) {
      // Логика входа
      console.log('Login data:', { email: formData.email, password: formData.password });
    } else {
      // Логика регистрации
      console.log('Register data:', formData);
    }
  };

  return (
    <div className={classes.container}>
      <div className={classes.logo}>socialnet</div>

      <h1 className={classes.title}>{isLogin ? 'Sign in to your account' : 'Create your account'}</h1>

      <form onSubmit={handleSubmit} className={classes.form}>
        {!isLogin && (
          <>
            <div className={classes.formGroup}>
              <label htmlFor="firstName" className={classes.label}>First Name</label>
              <input
                type="text"
                id="firstName"
                name="firstName"
                value={formData.firstName}
                onChange={handleChange}
                className={classes.input}
                required={!isLogin}
              />
            </div>

            <div className={classes.formGroup}>
              <label htmlFor="lastName" className={classes.label}>Last Name</label>
              <input
                type="text"
                id="lastName"
                name="lastName"
                value={formData.lastName}
                onChange={handleChange}
                className={classes.input}
                required={!isLogin}
              />
            </div>
          </>
        )}

        <div className={classes.formGroup}>
          <label htmlFor="email" className={classes.label}>Email</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className={classes.input}
            required
          />
        </div>

        <div className={classes.formGroup}>
          <label htmlFor="password" className={classes.label}>Password</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            className={classes.input}
            required
            minLength={6}
          />
        </div>

        <Button type="submit" className={classes.submitButton}>
          {isLogin ? 'Sign in' : 'Create Account'}
        </Button>
      </form>

      <div className={classes.switchAuth}>
        {isLogin ? (
          <>
            Don't have an account? <Link to="/register" className={classes.link}>Sign up</Link>
          </>
        ) : (
          <>
            Already have an account? <Link to="/login" className={classes.link}>Sign in</Link>
          </>
        )}
      </div>
    </div>
  );
};

export default AuthForm;
