import React from 'react';
import { Link } from 'react-router-dom';

import HeartIcon from '../HeartIcon/HeartIcon.jsx';
import ShareIcon from '../ShareIcon/ShareIcon.jsx';

import classes from './Post.module.css';

const Post = ({ post, isCurrentUserProfile, isGroupAdmin, onDelete, ...props }) => {
    const { content, imageUrl, author } = post;

    const handleDeleteClick = () => {
        if (window.confirm('Вы уверены, что хотите удалить этот пост?')) {
            onDelete(post.id);
        }
    };

    const shouldShowDeleteButton = isCurrentUserProfile || isGroupAdmin;

    return (
        <div className={classes.mainContainer}>
            <div className={classes.postHeader}>
                <div className={classes.postHeader__leftPart}>
                    <div className={classes.profilePictureContainer}>
                        <Link to={`/users/${author.id}`}>
                            <img src={author.avatarUrl} className={classes.profilePictureImage} alt="avatar" />
                        </Link>
                    </div>
                    <div className={classes.profileName}>
                        <Link to={`/users/${author.id}`} className={classes.profileLink}>
                            {author.firstName + " " + author.lastName}
                        </Link>
                        <p className={classes.dateOfCreation}>{post.createdAt}</p>
                    </div>
                </div>

                {shouldShowDeleteButton && (
                    <button 
                        className={classes.deleteButton}
                        onClick={handleDeleteClick}
                        title="Удалить пост"
                    >
                        <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                            <path d="M6 19C6 20.1 6.9 21 8 21H16C17.1 21 18 20.1 18 19V7H6V19ZM19 4H15.5L14.5 3H9.5L8.5 4H5V6H19V4Z" fill="#65676B"/>
                        </svg>
                    </button>
                )}
            </div>

            <div className={classes.divider} />
            <div className={classes.contentContainer}>
                {content && <p className={classes.content}>{content}</p>}
                <div className={classes.images}>
                    <img src={imageUrl} className={classes.image} alt="image"/>
                </div>
            </div>
            <div className={classes.divider} />
            <div className={classes.postFooter}>
                <div className={classes.postFooter__leftPart}>
                    <div className={classes.heartIcon}>
                        <HeartIcon/>
                        <p className={classes.likesCount}>{post.likeCount}</p>
                    </div>
                    <ShareIcon/>
                </div>
            </div>
        </div>
    );
};

export default Post;
