import React from 'react';
import { Link } from 'react-router-dom';
import classes from './FriendItem.module.css'; // Убедитесь, что CSS файл существует

const FriendItem = ({ user, pageType, onAction }) => {
    // user здесь - это объект с данными пользователя (id, firstName, lastName, avatarUrl)
    // и requestId, если это заявка.

    return (
        <div className={classes.userCard}>
            <div className={classes.userInfo}>
                <Link to={`/users/${user.id}`}>
                    <img 
                        src={user.avatarUrl || '/default-avatar.png'} 
                        alt="avatar"
                        className={classes.avatar}
                    />
                </Link>
                <div>
                    <Link to={`/users/${user.id}`} className={classes.userNameLink}>
                        <h3>{user.firstName} {user.lastName}</h3>
                    </Link>
                    {user.mutualFriendsCount > 0 && 
                        <p>Общих друзей: {user.mutualFriendsCount}</p>
                    }
                </div>
            </div>

            {/* Кнопки действий в зависимости от типа страницы */}
            {/* onAction вызывается с именем действия. ID уже зафиксирован в FriendsPage */}
            {pageType === 'friends' && ( // Мои друзья
                <button 
                    className={classes.actionButton}
                    onClick={() => onAction('remove')} // ID друга (user.id) будет использован в обработчике
                >
                    Удалить
                </button>
            )}

            {pageType === 'sentRequests' && ( // Отправленные мной заявки
                <button 
                    className={classes.actionButton}
                    onClick={() => onAction('cancel')} // requestId будет использован в обработчике
                >
                    Отменить заявку
                </button>
            )}

            {pageType === 'receivedRequests' && ( // Полученные мной заявки
                <div className={classes.requestActions}>
                    <button 
                        className={`${classes.actionButton} ${classes.acceptButton}`}
                        onClick={() => onAction('accept')} // requestId
                    >
                        Принять
                    </button>
                    <button 
                        className={`${classes.actionButton} ${classes.rejectButton}`}
                        onClick={() => onAction('reject')} // requestId
                    >
                        Отклонить
                    </button>
                </div>
            )}
            {/* pageType === 'viewOnly' (друзья другого пользователя) - нет кнопок действий отсюда */}
        </div>
    );
};

export default FriendItem;
