import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { observer } from 'mobx-react-lite';
import { Context } from '../../storage';
import classes from './Header.module.css'; // CSS
import { ROUTE } from '../../utils/consts';
import Button from '../Button/Button'; // Если нужна кнопка выхода

const Header = observer(() => {
    const { userStore } = useContext(Context);
    const navigate = useNavigate();

    const handleLogout = () => {
        userStore.logout();
        navigate(ROUTE.LOGIN);
    };

    return (
        <header className={classes.headerContainer}> {/* Добавьте класс для стилизации header */}
            <div className={classes.container}>
                <div>
                    <Link to={ROUTE.FEED}>
                        <img src="/logo.png" alt="logo" className={classes.logo}/>
                    </Link>
                </div>
                {/* Поиск можно добавить сюда */}
                <div className={classes.actions}>
                    {userStore.isAuth && userStore.user ? (
                        <>
                            <Link to={ROUTE.POST_CREATE} className={classes.createPostLink}> {/* Или иконка */}
                                Create Post
                            </Link>
                            <Link to={ROUTE.ME /* или /users/${userStore.userId} */} className={classes.profileLink}>
                                <img 
                                    src={userStore.user.avatarUrl || "/default_avatar.png"} 
                                    alt="avatar" 
                                    className={classes.avatar}
                                />
                                <span className={classes.userName}>{userStore.user.firstName}</span>
                            </Link>
                            <Button onClick={handleLogout} white className={classes.logoutButton}>Logout</Button>
                        </>
                    ) : (
                        <>
                            <Link to={ROUTE.LOGIN} className={classes.authLink}>Login</Link>
                            <Link to={ROUTE.REGISTER} className={classes.authLink}>Register</Link>
                        </>
                    )}
                </div>
            </div>
        </header>
    );
});

export default Header;
