import { makeAutoObservable } from "mobx";

export class UserStorage {
    constructor() {
        this._isAuth = false;
        this._user = {};
        this._currentProfile = null; // Профиль просматриваемого пользователя
        this._searchResults = []; // Результаты поиска пользователей

        makeAutoObservable(this);
    }

    // Setters
    setIsAuth(bool) {
        this._isAuth = bool;
    }

    setUser(user) {
        this._user = user;
    }

    setCurrentProfile(user) {
        this._currentProfile = user;
    }

    setSearchResults(users) {
        this._searchResults = users;
    }

    // Getters
    get isAuth() {
        return this._isAuth;
    }

    get user() {
        return this._user;
    }

    get currentProfile() {
        return this._currentProfile;
    }

    get searchResults() {
        return this._searchResults;
    }
}
