import { makeAutoObservable } from "mobx";

export class GroupStorage {
    constructor() {
        this._groups = []; // Все группы
        this._userGroups = []; // Группы пользователя
        this._currentGroup = null; // Текущая просматриваемая группа
        this._groupPosts = []; // Посты в группе

        makeAutoObservable(this);
    }

    setGroups(groups) {
        this._groups = groups;
    }

    setUserGroups(groups) {
        this._userGroups = groups;
    }

    setCurrentGroup(group) {
        this._currentGroup = group;
    }

    setGroupPosts(posts) {
        this._groupPosts = posts;
    }

    get groups() {
        return this._groups;
    }

    get userGroups() {
        return this._userGroups;
    }

    get currentGroup() {
        return this._currentGroup;
    }

    get groupPosts() {
        return this._groupPosts;
    }
}
