import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

import Button from '../Button/Button';
import classes from './GroupForm.module.css';

const GroupForm = ({ isEdit = false }) => {
    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        name: '',
        description: '',
        photo: null,
        changePhoto: false
    });

    // Загрузка данных группы при редактировании
    useEffect(() => {
        if (isEdit) {
            // Загрузка данных группы по ID
            fetchGroupData(id).then(data => {
                setFormData({
                    name: data.name,
                    description: data.description,
                    photo: null,
                    changePhoto: false
                });
            });
        }
    }, [id, isEdit]);

    const handleChange = (e) => {
        const { name, value, type, files } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'file' ? files[0] : value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (isEdit) {
            // Логика обновления группы
            await updateGroup(id, formData);
        } else {
            // Логика создания группы
            await createGroup(formData);
        }

        navigate(isEdit ? `/groups/${id}` : '/groups');
    };

    return (
        <div className={classes.container}>
            <h1 className={classes.title}>
                {isEdit ? 'Edit Group Information' : 'Create New Group'}
            </h1>

            <form onSubmit={handleSubmit} className={classes.form}>
                {isEdit && (
                    <div className={classes.field}>
                        <label className={classes.checkboxLabel}>
                            <input 
                                type="checkbox"
                                name="changePhoto"
                                checked={formData.changePhoto}
                                onChange={() => setFormData(prev => ({
                                    ...prev,
                                    changePhoto: !prev.changePhoto
                                }))}
                                className={classes.checkbox}
                            />
                            Change Group Photo
                        </label>
                        {formData.changePhoto && (
                            <input 
                                type="file"
                                name="photo"
                                onChange={handleChange}
                                accept="image/*"
                                className={classes.fileInput}
                            />
                        )}
                    </div>
                )}

                {!isEdit && (
                    <div className={classes.field}>
                        <label className={classes.label}>Group Photo</label>
                        <input 
                            type="file"
                            name="photo"
                            onChange={handleChange}
                            accept="image/*"
                            className={classes.fileInput}
                            required={!isEdit}
                        />
                    </div>
                )}

                <div className={classes.field}>
                    <label className={classes.label}>Group Name</label>
                    <input
                        type="text"
                        name="name"
                        value={formData.name}
                        onChange={handleChange}
                        className={classes.input}
                        required
                    />
                </div>

                <div className={classes.field}>
                    <label className={classes.label}>Description</label>
                    <textarea
                        name="description"
                        value={formData.description}
                        onChange={handleChange}
                        className={classes.textarea}
                        rows={4}
                    />
                </div>

                <div className={classes.actions}>
                    <Button type="submit">
                        {isEdit ? 'Save Changes' : 'Create Group'}
                    </Button>
                    <Button 
                        white 
                        type="button"
                        onClick={() => navigate(-1)}
                    >
                        Cancel
                    </Button>
                </div>
            </form>
        </div>
    );
};

