import React, { useContext, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

import classes from './Main.module.css';

import PostContainer from '../../component/PostContainer/PostContainer';
import MainLeftSideBar from '../../component/MainLeftSideBar/MainLeftSideBar';
import MainRightSideBar from '../../component/MainRightSideBar/MainRightSideBar';

import { Context } from '../..';

const Main = () => {
    const { postStorage } = useContext(Context);
    const location = useLocation();

    const getPosts = () => {
        switch(location.pathname) {
            case '/recommendations':
                return postStorage.recommendedPosts.length > 0 
                    ? postStorage.recommendedPosts 
                    : postStorage.posts;
            case '/feed':
            default:
                return postStorage.posts;
        }
    };

    const posts = getPosts();

    return (
        <main className={classes.mainContainer}>
            <MainLeftSideBar />
            <div className={classes.container}>
                <PostContainer posts={posts} />
            </div>
            <MainRightSideBar />
        </main>
    );
};

export default Main;
