import React, { useContext } from 'react';
import { Context } from '../..';
import Post from '../Post/Post';

import classes from './PostContainer.module.css'

const PostContainer = ({posts, isCurrentUserProfile = false, isGroupAdmin = false}) => {
    const { userStorage } = useContext(Context);

    return (
        <div className={classes.container}>
            {posts.map((post) => (
                <Post 
                    post={post} 
                    key={post.id}
                    isCurrentUserProfile={isCurrentUserProfile && userStorage.userId === post.author.id}
                    isGroupAdmin={isGroupAdmin}
                    onDelete={(postId) => {
                        // Здесь можно вызвать метод из хранилища для удаления поста
                    }}
                />
            ))}
        </div>
    );
};

export default PostContainer;
