import React, { useState, useRef, useContext } from 'react';
import { observer } from 'mobx-react-lite';
import { useNavigate } from 'react-router-dom';
import { Context } from '../../storage';
import classes from './CreatePost.module.css';
const CloseIcon = () => <span style={{cursor: 'pointer', fontWeight: 'bold'}}>X</span>;

const CreatePost = observer(() => {
    const { userStore, postStore } = useContext(Context);
    const navigate = useNavigate();
    const user = userStore.user;

    const [content, setContent] = useState('');
    const [image, setImage] = useState(null); // File object
    const [imagePreview, setImagePreview] = useState(null);
    const fileInputRef = useRef(null);

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setImage(file);
            const reader = new FileReader();
            reader.onloadend = () => {
                setImagePreview(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleRemoveImage = () => {
        setImage(null);
        setImagePreview(null);
        if (fileInputRef.current) {
            fileInputRef.current.value = '';
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!content && !image) {
            alert("Post cannot be empty.");
            return;
        }
        const success = await postStore.createPost(content, image);
        if (success) {
            setContent('');
            setImage(null);
            setImagePreview(null);
            navigate(-1);
        } else {
            alert(`Error creating post: ${postStore.error}`);
        }
    };

    const triggerFileInput = () => {
        fileInputRef.current.click();
    };

    if (!userStore.isAuth || !user?.id) {
        navigate(ROUTE.LOGIN);
        return <div>Please login to create a post.</div>;
    }

    return (
        <div className={classes.pageContainer}>
            <form onSubmit={handleSubmit} className={classes.postForm}>
                <div className={classes.mainContainer}>
                    <div className={classes.postHeader}>
                        <div className={classes.postHeader__leftPart}>
                            <div className={classes.profilePictureContainer}>
                                <img 
                                    src={user.avatarUrl || '/default-avatar.png'} 
                                    className={classes.profilePictureImage} 
                                    alt="avatar" 
                                />
                            </div>
                            <div className={classes.profileName}>
                                <span className={classes.profileLink}>
                                    {user.firstName} {user.lastName}
                                </span>
                                <p className={classes.dateOfCreation}>Now</p>
                            </div>
                        </div>
                    </div>

                    <div className={classes.divider} />

                    <div className={classes.contentContainer}>
                        <textarea
                            className={classes.contentInput}
                            placeholder="What's on your mind?"
                            value={content}
                            onChange={(e) => setContent(e.target.value)}
                        />

                        {imagePreview && (
                            <div className={classes.imagePreviewContainer}>
                                <div className={classes.removeImageButton} onClick={handleRemoveImage}>
                                    <CloseIcon className={classes.closeIcon} />
                                </div>
                                <img 
                                    src={imagePreview} 
                                    className={classes.image} 
                                    alt="preview" 
                                />
                            </div>
                        )}
                    </div>

                    <div className={classes.divider} />

                    <div className={classes.postFooter}>
                        <div className={classes.postFooter__leftPart}>
                            <input
                                type="file"
                                ref={fileInputRef}
                                onChange={handleImageChange}
                                accept="image/*"
                                style={{ display: 'none' }}
                            />
                            <button 
                                type="button" 
                                className={classes.mediaButton}
                                onClick={triggerFileInput}
                            >
                                Photo
                            </button>
                        </div>
                        <button 
                            type="submit" 
                            className={classes.submitButton}
                            disabled={(!content && !image) || postStore.loading}
                        >
                            {postStore.loading ? 'Posting...' : 'Post'}
                        </button>
                    </div>
                </div>
            </form>
        </div>
    );
});

export default CreatePost;
