import React, { useState } from 'react';
import Button from '../../component/Button/Button';
import classes from './EditGroupPage.module.css';

const EditGroup = () => {
    const [groupName, setGroupName] = useState('Tech Enthusiasts');
    const [description, setDescription] = useState('');
    const [changePhoto, setChangePhoto] = useState(false);

    const handleSubmit = (e) => {
        e.preventDefault();
        // Логика сохранения изменений
        console.log({ groupName, description, changePhoto });
    };

    return (
        <div className={classes.container}>
            <h1 className={classes.title}>Edit Group Information</h1>

            <form onSubmit={handleSubmit} className={classes.form}>
                <div className={classes.field}>
                    <label className={classes.checkboxLabel}>
                        <input 
                            type="checkbox" 
                            checked={changePhoto}
                            onChange={() => setChangePhoto(!changePhoto)}
                            className={classes.checkbox}
                        />
                        Change Group Photo
                    </label>
                    {changePhoto && (
                        <input 
                            type="file" 
                            accept="image/*"
                            className={classes.fileInput}
                        />
                    )}
                </div>

                <div className={classes.field}>
                    <label className={classes.label}>Group Name</label>
                    <input
                        type="text"
                        value={groupName}
                        onChange={(e) => setGroupName(e.target.value)}
                        className={classes.input}
                    />
                </div>

                <div className={classes.field}>
                    <label className={classes.label}>Description</label>
                    <textarea
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        className={classes.textarea}
                        rows={4}
                    />
                </div>

                <div className={classes.actions}>
                    <Button type="submit">Save Changes</Button>
                    <Button white type="button">Cancel</Button>
                </div>
            </form>
        </div>
    );
};

export default EditGroup;
