import { makeAutoObservable } from "mobx";

export class PostStorage {
    constructor() {
        this._posts = [
            {
                "id": 1,
                "content": "Some text here",
                "imageUrl": "https://png.pngtree.com/thumb_back/fw800/background/20230610/pngtree-picture-of-a-blue-bird-on-a-black-background-image_2937385.jpg",
                "createdAt": "2025-05-04T17:38:08.327416",
                "author": {
                    "id": 1,
                    "avatarUrl": "https://png.pngtree.com/thumb_back/fw800/background/20230610/pngtree-picture-of-a-blue-bird-on-a-black-background-image_2937385.jpg",
                    "firstName": "Bob",
                    "lastName": "Hope"
                },
                "group": null,
                "likeCount": 123,
                "likedByCurrentUser": false
            },
            {
                "id": 2,
                "content": "Some text here",
                "imageUrl": "https://png.pngtree.com/thumb_back/fw800/background/20230610/pngtree-picture-of-a-blue-bird-on-a-black-background-image_2937385.jpg",
                "createdAt": "2025-05-04T17:38:08.327416",
                "author": {
                    "id": 1,
                    "avatarUrl": "https://png.pngtree.com/thumb_back/fw800/background/20230610/pngtree-picture-of-a-blue-bird-on-a-black-background-image_2937385.jpg",
                    "firstName": "Bob",
                    "lastName": "Hope"
                },
                "group": null,
                "likeCount": 123,
                "likedByCurrentUser": false
            },
            {
                "id": 3,
                "content": "Some text here",
                "imageUrl": "https://png.pngtree.com/thumb_back/fw800/background/20230610/pngtree-picture-of-a-blue-bird-on-a-black-background-image_2937385.jpg",
                "createdAt": "2025-05-04T17:38:08.327416",
                "author": {
                    "id": 1,
                    "avatarUrl": "https://png.pngtree.com/thumb_back/fw800/background/20230610/pngtree-picture-of-a-blue-bird-on-a-black-background-image_2937385.jpg",
                    "firstName": "Bob",
                    "lastName": "Hope"
                },
                "group": null,
                "likeCount": 123,
                "likedByCurrentUser": false
            },

        ]; // Все посты (лента)
        this._userPosts = []; // Посты конкретного пользователя
        this._recommendedPosts = []; // Рекомендованные посты
        this._currentPost = null; // Текущий просматриваемый пост

        makeAutoObservable(this);
    }

    setPosts(posts) {
        this._posts = posts;
    }

    setUserPosts(posts) {
        this._userPosts = posts;
    }

    setRecommendedPosts(posts) {
        this._recommendedPosts = posts;
    }

    setCurrentPost(post) {
        this._currentPost = post;
    }

    addLike(postId) {
        const post = this._posts.find(p => p.id === postId);
        if (post) {
            post.likesCount += 1;
        }
    }

    get posts() {
        return this._posts;
    }

    get userPosts() {
        return this._userPosts;
    }

    get recommendedPosts() {
        return this._recommendedPosts;
    }

    get currentPost() {
        return this._currentPost;
    }
}
