import React, { useContext } from 'react';
import { observer } from 'mobx-react-lite';
// import { Context } from '../../storage'; // Не нужен, так как Post сам берет из Context
import Post from '../Post/Post';
import classes from './PostContainer.module.css'; // Убедитесь, что CSS файл существует

// isGroupContext - флаг, что посты отображаются на странице группы
// (чтобы не показывать ссылку на группу в каждом посте)
const PostContainer = observer(({ posts, isGroupContext = false }) => {
    // const { postStore } = useContext(Context); // Не нужен здесь, если Post сам берет

    if (!posts || posts.length === 0) {
        return <div className={classes.container}><p>No posts to display.</p></div>;
    }

    return (
        <div className={classes.container}>
            {posts.content.map((post) => (
                <Post 
                    post={post} 
                    key={post.id}
                    isGroupContext={isGroupContext}
                    // onDelete больше не нужен, Post сам обработает через store
                />
            ))}
        </div>
    );
});

export default PostContainer;
