import React, { useContext } from 'react';
import { Link } from 'react-router-dom';

import classes from './ProfilePageSideBar.module.css'
import { Context } from '../..';

const ProfilePageSideBar = (props) => {
    const { friendStorage, groupStorage } = useContext(Context)
    const friends = friendStorage.friends || [];
    const groups = groupStorage.userGroups

    return (
        <div className={classes.mainContainer}>
            <div className={classes.container}>
                <h2 className={classes.title}>Друзья</h2>
                <div className={classes.items}>
                    {friends.length > 0 ? friends.map((friend) => (
                        <div className={classes.item}>
                            <div className={classes.avatarContainer}>
                                <img src={friend.avatarUrl} alt="avatar" className={classes.avatar}/>
                            </div>
                            <div className={classes.linkContainer}>
                                <Link to={`/users/${friend.id}`} className={classes.link}>
                                    {`${friend.firstName} ${friend.lastName}`}
                                </Link>
                            </div>
                        </div>
                    )) : (<div>Пока ничего нет</div>)}
                </div>
            </div>

            <div className={classes.container}>
                <h2 className={classes.title}>Группы</h2>
                <div className={classes.items}>
                    {groups.length > 0 ? groups.map((group) => (
                        <div className={classes.item}>
                            <div className={classes.avatarContainer}>
                                <img src={group.imageUrl} alt="avatar" className={classes.avatar}/>
                            </div>
                            <div className={classes.linkContainer}>
                                <Link to={`/groups/${group.id}`} className={classes.link}>
                                    {group.name}
                                </Link>
                            </div>
                        </div>
                    )) : (<div>Пока ничего нет</div>)}
                </div>
            </div>
        </div>
    );
};

export default ProfilePageSideBar;
